emailjs.init("87nNzzvvok51teR3t");

document.getElementById("contactForm").addEventListener("submit", function(event) {
    event.preventDefault(); // Empêche le rechargement de la page

    // Récupération des valeurs du formulaire
    const name = document.getElementById("name").value;
    const email = document.getElementById("email").value;
    const numero = document.getElementById("numero").value;
    const message = document.getElementById("message").value;

    // Préparer les données à envoyer
    const templateParams = {
        to_email: "jacquesloemba@outlook.fr",
        from_name: name,
        from_email: email,
        numero: numero,
        message: message
    };

    // Envoyer l'email avec EmailJS
    emailjs.send("service_hmjhxjc", "template_j5urlpj", templateParams)
        .then(function(response) {
            console.log("Email envoyé avec succès !", response.status, response.text);
            alert("Votre message a bien été envoyé, merci " + name + " !");
            document.getElementById("contactForm").reset(); // Réinitialiser le formulaire
        }, function(error) {
            console.error("Erreur lors de l'envoi de l'email :", error);
            alert("Une erreur s'est produite lors de l'envoi. Veuillez réessayer.");
        });
});
